choose.when:
	WHERE b.writer = m.name 
          AND b.show = 'y'
		  AND #{map.search_option} LIKE #{map.keyword}

choose.otherwise:
	WHERE b.writer = m.name
 		AND b.show = 'y'
 		AND (title LIKE #{map.keyword} 
 			 OR content LIKE #{map.keyword}  
 			 OR name LIKE #{map.keyword})

mapper.update:
UPDATE tbl_board
	SET viewcnt = viewcnt + 1
WHERE bno = #{bno}

UPDATE tbl_board
	SET show ='n'
WHERE bno = #{bno}
